/* jshint node: true */
/* global describe, it */

'use strict';

var expect     = require('chai').expect,
    gutil      = require('gulp-util'),
    unpathify  = require('./index'),
    Stream     = require('stream'),
    es         = require('event-stream'),

    fixture    = require('fs').readFileSync('./fixture.js', 'utf-8'),
    result     = 'var bar = require(1)';

describe('gulp-unpathify', function() {
    it('should strip require paths out of a browserify bundle', function (cb) {
        var stream = unpathify();

        stream.on('data', function(data) {
            expect(String(data.contents)).to.contain(result);
            cb();
        });

        stream.write(new gutil.File({
            contents: new Buffer(fixture)
        }));
    });

    it('should work the same in stream mode', function(cb) {
        var stream = unpathify();
        var fakeFile = new gutil.File({
            contents: new Stream()
        });

        stream.on('data', function(data) {
            data.contents.pipe(es.wait(function(err, data) {
                expect(data).to.contain(result);
                cb();
            }));
        });

        stream.write(fakeFile);
        fakeFile.contents.write(fixture);
        fakeFile.contents.end();
    });

    it('should let null files pass through', function(cb) {
        var stream = unpathify(),
            n = 0;
        stream.pipe(es.through(function(file) {
            expect(file.path).to.equal('null.md');
            expect(file.contents).to.equal(null);
            n++;
        }, function() {
            expect(n).to.equal(1);
            cb();
        }));
        stream.write(new gutil.File({
            path: 'null.md',
            contents: null
        }));
        stream.end();
    });
});
